      let source_x =           (value % 8) * 16;
      let source_y = Math.floor(value / 17) * 16;
      let destination_x =           (index % 40) * 16;
      let destination_y = Math.floor(index / 40) * 16;

    let backgroundRectangle = new PIXI.Rectangle(source_x, source_y, 16, 16); //sets the map frame inside the tileSet, x, y, width of ractangle, height of ractangle
